INSERT INTO public.announcement ("Id", "Title", "Description", "ImageUrl", "IsPushNotification", "ScheduledTime", "Status", "CreatedBy", "CreatedAt", "UpdatedAt", "Audience") VALUES (16, 'Celebrating Excellence: The Legendary Team Award', 'We are proud to announce the launch of the Legendary Team Award, a prestigious recognition for the teams that have demonstrated extraordinary resilience, exceptional collaboration, and outstanding achievements over the years. This award honors long-standing teams that have not only succeeded on difficult projects but have also shown unwavering dedication and commitment to excellence. These teams have faced challenges head-on, pushed the boundaries of success, and left a lasting impact on our organisation. Nominate your team today and let us celebrate the legends who inspire us all with their passion, innovation, and teamwork.', 'https://awardstoragefyp.blob.core.windows.net/videos/7362a698-d473-4a77-9f71-7507ca1655d7_513cd047-8ebc-4927-abdc-fab898a0b4a5.png', true, null, 'draft', 2, '2025-05-10 19:53:37.323537', '2025-05-10 18:26:41.839433', 'MobileUsers');
INSERT INTO public.announcement ("Id", "Title", "Description", "ImageUrl", "IsPushNotification", "ScheduledTime", "Status", "CreatedBy", "CreatedAt", "UpdatedAt", "Audience") VALUES (18, 'Best Innovator Award Winner Announced!', 'We are thrilled to announce the recipient of the Best Innovator Award! This individual has demonstrated exceptional creativity, transforming ideas into impactful solutions that have driven our organisation forward. Their ability to challenge the status quo, introduce cutting-edge strategies, and inspire innovative thinking has set them apart as a true leader in driving change. Please join us in congratulating our winner for their outstanding contributions, and thank them for continuously inspiring us to think outside the box!', 'https://awardstoragefyp.blob.core.windows.net/videos/4c6a454b-711f-432d-a3c5-1cb98e4db8ab_hand-drawn-iranian-women-illustration_23-2149855924.avif', false, null, 'published', 1, '2025-05-10 19:02:19.741792', '2025-05-10 18:29:52.959047', 'MobileUsers');
INSERT INTO public.announcement ("Id", "Title", "Description", "ImageUrl", "IsPushNotification", "ScheduledTime", "Status", "CreatedBy", "CreatedAt", "UpdatedAt", "Audience") VALUES (6, 'Nomination reminder', 'Have you made your nominations yet? Don''t forget our nomination window closes on Friday!', 'https://awardstoragefyp.blob.core.windows.net/videos/e87dae0b-f479-40af-940d-b9915f6fccf8_woman-waving-her-webcam-concept_23-2148489795.jpg', true, '2025-05-10 19:15:00.000000', 'published', 1, '2025-05-04 09:42:08.495518', '2025-05-10 18:30:00.351118', 'MobileUsers');
INSERT INTO public.announcement ("Id", "Title", "Description", "ImageUrl", "IsPushNotification", "ScheduledTime", "Status", "CreatedBy", "CreatedAt", "UpdatedAt", "Audience") VALUES (1, 'Announcing the Top Team Award: Celebrating Excellence in Collaboration', 'We are excited to introduce the Top Team Award, a new honour that recognises teams who have consistently delivered outstanding results through exceptional collaboration, innovation, and dedication. This award celebrates the power of teamwork, highlighting those groups that have excelled in achieving goals, overcoming challenges, and driving success across the organisation. Whether it''s through groundbreaking projects, remarkable performance, or seamless teamwork, the Top Team Award will spotlight the teams that set the standard for excellence. Nominate your team today and join us in celebrating the champions of collaboration!', 'https://awardstoragefyp.blob.core.windows.net/videos/24765ff4-8571-4577-a855-6cdd66b89a4d_Screenshot 2024-11-30 171447.png', true, '2025-05-30 18:00:00.000000', 'published', 4, '2025-04-13 23:31:16.363364', '2025-05-10 17:57:54.466929', 'MobileUsers');
INSERT INTO public.announcement ("Id", "Title", "Description", "ImageUrl", "IsPushNotification", "ScheduledTime", "Status", "CreatedBy", "CreatedAt", "UpdatedAt", "Audience") VALUES (19, 'Outstanding Achievement Award Nominations Open', 'The Outstanding Achievement Award is now open for nominations! This award recognizes teams who have made extraordinary contributions to the organization, showcasing exceptional dedication and delivering results that have significantly impacted our success. Whether overcoming major challenges, achieving ambitious goals, or setting new standards of excellence, this award honours teams that go above and beyond. Submit your nominations today and help us recognise the remarkable teams whose achievements deserve to be celebrated!', 'https://awardstoragefyp.blob.core.windows.net/videos/1c566bee-cfe4-4bb7-acbd-e8504f022607_happy-man-his-desk-talking-his-friends_23-2148489794.avif', false, null, 'draft', 5, '2025-05-10 18:29:28.723986', '2025-05-10 18:29:28.723986', 'MobileUsers');
