INSERT INTO public."awardCategory" ("Id", "Name", "Type", "SponsorId", "IntroductionVideo", "IntroductionParagraph", "ProfileStatus", "CreatedAt", "UpdatedAt", "AwardProcessId") VALUES (3, 'Outstanding Achievement', 'Individual', 5, 'https://awardstoragefyp.blob.core.windows.net/videos/fd980772-06bb-433e-b350-2ddb596ea356_232538_small.mp4', 'The Outstanding Achievement Award honours individuals or teams who have made extraordinary contributions to the organisation through their exceptional accomplishments. This award recognises those who have gone above and beyond in their efforts, delivering results that significantly impact the organisationâ€™s success. Whether through overcoming major challenges, driving innovation, or achieving remarkable milestones, the Outstanding Achievement Award celebrates individuals or teams whose dedication, persistence, and excellence have set them apart and made a lasting impact.', 'published', '2025-05-07 22:18:24.714540', '2025-05-10 17:51:10.251322', 1);
INSERT INTO public."awardCategory" ("Id", "Name", "Type", "SponsorId", "IntroductionVideo", "IntroductionParagraph", "ProfileStatus", "CreatedAt", "UpdatedAt", "AwardProcessId") VALUES (2, 'Top Team', 'Team', 4, 'https://awardstoragefyp.blob.core.windows.net/videos/4b272160-9d72-4c46-970f-5c91b57b09a0_205691-927672681_small.mp4', 'The Top Team Award recognises a team that has consistently demonstrated exceptional performance, collaboration, and results. This award celebrates teams that have excelled in achieving their goals, overcoming challenges, and contributing significantly to the success of the organisation. Whether through innovation, teamwork, or leadership, the Top Team has shown remarkable synergy and achieved outstanding outcomes. This award honours teams that work cohesively, support one another, and deliver exceptional results, setting the standard for teamwork and excellence across the organisation.', 'draft', '2025-04-14 09:31:16.063890', '2025-05-10 17:45:04.268536', 1);
